	/**
	 * Add a TableMap instance to the database for this peer class.
	 */
	public static function buildTableMap()
	{
	  $dbMap = Propel::getDatabaseMap(BaseFuentesPeer::DATABASE_NAME);
	  if (!$dbMap->hasTable(BaseFuentesPeer::TABLE_NAME))
	  {
	    $dbMap->addTableObject(new FuentesTableMap());
	  }
