	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAdministrativafinanciera(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		GestionresultadoPeer::addSelectColumns($criteria);
		$startcol = (GestionresultadoPeer::NUM_COLUMNS - GestionresultadoPeer::NUM_LAZY_LOAD_COLUMNS);
		AdministrativafinancieraPeer::addSelectColumns($criteria);

		$criteria->addJoin(GestionresultadoPeer::GRE_IAF_ID, AdministrativafinancieraPeer::IAF_ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseGestionresultadoPeer', $criteria, $con);
		}

		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = GestionresultadoPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = GestionresultadoPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {

				$cls = GestionresultadoPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				GestionresultadoPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded

			$key2 = AdministrativafinancieraPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
				$obj2 = AdministrativafinancieraPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = AdministrativafinancieraPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					AdministrativafinancieraPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded
				
				// Add the $obj1 (Gestionresultado) to $obj2 (Administrativafinanciera)
				$obj2->addGestionresultado($obj1);

			} // if joined row was not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


	/**
	 * Returns the number of rows matching criteria, joining all related tables
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct Whether to select only distinct columns; deprecated: use Criteria->setDistinct() instead.
	 * @param      PropelPDO $con
