	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collInformacionvisuals) {
				foreach ((array) $this->collInformacionvisuals as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPrestadorporservicios) {
				foreach ((array) $this->collPrestadorporservicios as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collInformacionvisuals = null;
		$this->collPrestadorporservicios = null;
			$this->aRango = null;
			$this->aUsuario = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BasePrestador:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BasePrestador::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BasePrestador
